CREATE VIEW `CUSTOMER_VIEW` AS select `cust`.`CUSTOMERS_ID` AS `CUSTOMERS_ID`,`cust`.`UNIT_KERJA_ID` AS `UNIT_KERJA_ID`,`ukj`.`UNIT_KERJA_NAME` AS `UNIT_KERJA_NAME`,`cust`.`CABANG_ID` AS `CABANG_ID`,`cb`.`CABANG_NAME` AS `CABANG_NAME`,`cust`.`PAYMENT_POINT_ID` AS `PAYMENT_POINT_ID`,`ppt`.`PAYMENT_POINT_NAME` AS `PAYMENT_POINT_NAME`,`ppt`.`PAYMENT_POINT_CODE` AS `PAYMENT_POINT_CODE`,`cust`.`CUSTOMERS_NO_REG` AS `CUSTOMERS_NO_REG`,`cust`.`CUSTOMERS_NAME` AS `CUSTOMERS_NAME`,`cust`.`CUSTOMERS_ADDRESS` AS `CUSTOMERS_ADDRESS`,`cust`.`CUSTOMERS_NO_KTP` AS `CUSTOMERS_NO_KTP`,`cust`.`CUSTOMERS_PHONE` AS `CUSTOMERS_PHONE`,`cust`.`CUSTOMERS_BIRTHDATE` AS `CUSTOMERS_BIRTHDATE`,`cust`.`CUSTOMERS_NOPEN` AS `CUSTOMERS_NOPEN`,`cust`.`CUSTOMERS_COMPANY` AS `CUSTOMERS_COMPANY`,`cust`.`CUSTOMERS_COMPANY_ADDRESS` AS `CUSTOMERS_COMPANY_ADDRESS`,`cust`.`CUSTOMERS_COMPANY_PHONE` AS `CUSTOMERS_COMPANY_PHONE`,`cust`.`CUSTOMERS_COMPANY_POSITION` AS `CUSTOMERS_COMPANY_POSITION`,`cust`.`CUSTOMERS_REFERENCE_NAME` AS `CUSTOMERS_REFERENCE_NAME`,`cust`.`CUSTOMERS_REFERENCE_ADDRESS` AS `CUSTOMERS_REFERENCE_ADDRESS`,`cust`.`CUSTOMERS_REFERENCE_PHONE` AS `CUSTOMERS_REFERENCE_PHONE`,`cust`.`CUSTOMERS_REFERENCE_STATUS` AS `CUSTOMERS_REFERENCE_STATUS`,`cust`.`CREATED_DATE` AS `CREATED_DATE`,`cust`.`MODIFIED_DATE` AS `MODIFIED_DATE` from (((`CUSTOMERS` `cust` join `CABANG` `cb`) join `UNIT_KERJA` `ukj`) join `PAYMENT_POINT` `ppt`) where ((`cust`.`CABANG_ID` = `cb`.`CABANG_ID`) and (`cust`.`UNIT_KERJA_ID` = `ukj`.`UNIT_KERJA_ID`) and (`cust`.`PAYMENT_POINT_ID` = `ppt`.`PAYMENT_POINT_ID`));
